  package com.jojoldu.book.springboot.config.auth;

  import com.jojoldu.book.springboot.config.auth.dto.OAuthAttributes;
  import com.jojoldu.book.springboot.config.auth.dto.SessionUser;
  import com.jojoldu.book.springboot.domain.user.User;
  import com.jojoldu.book.springboot.domain.user.UserRepository;
  import lombok.RequiredArgsConstructor;
  import org.springframework.security.core.authority.SimpleGrantedAuthority;
  import org.springframework.security.oauth2.client.userinfo.DefaultOAuth2UserService;
  import org.springframework.security.oauth2.client.userinfo.OAuth2UserRequest;
  import org.springframework.security.oauth2.client.userinfo.OAuth2UserService;
  import org.springframework.security.oauth2.core.OAuth2AuthenticationException;
  import org.springframework.security.oauth2.core.user.DefaultOAuth2User;
  import org.springframework.security.oauth2.core.user.OAuth2User;
  import org.springframework.stereotype.Service;

  import javax.servlet.http.HttpSession;
  import java.util.Collections;

  @RequiredArgsConstructor
  @Service
  public class CustomOAuth2UserService implements OAuth2UserService<OAuth2UserRequest, OAuth2User>
  {
      private final UserRepository userRepository;
      private final HttpSession httpSession;

      @Override
      public OAuth2User loadUser(OAuth2UserRequest userRequest) throws OAuth2AuthenticationException
      {
          OAuth2UserService<OAuth2UserRequest, OAuth2User> delegate = new DefaultOAuth2UserService();
          OAuth2User oAuth2User = delegate.loadUser(userRequest);

          String registrationId = userRequest.getClientRegistration().getRegistrationId(); // í˜„ì¬ ë¡œê·¸ì¸ ì§„í–‰ ì¤‘ì¸ ì„œë¹„ìŠ¤ êµ¬ë¶„(ë³µìˆ˜ê°œì˜ ì†Œì…œ ë¡œê·¸ì¸ ì‚¬ìš©ì‹œ í•„ìš”)
          String userNameAttributeName = userRequest.getClientRegistration().getProviderDetails().getUserInfoEndpoint().getUserNameAttributeName(); // OAuth2 ë¡œê·¸ì¸ ì§„í–‰ ì‹œ í‚¤ê°€ ë˜ëŠ” í•„ë“œê°’(Primary key)

          OAuthAttributes attributes = OAuthAttributes.of(registrationId, userNameAttributeName, oAuth2User.getAttributes()); // OAuth2UserServiceë¥¼ í†µí•´ ê°€ì ¸ì˜¨ OAuth2Userì˜ attributeë¥¼ ë‹´ì„ í´ë˜ìŠ¤

          User user = saveOrUpdate(attributes);
          httpSession.setAttribute("user", new SessionUser(user)); // ì„¸ì…˜ì— ì‚¬ìš©ì ì •ë³´ë¥¼ ì €ì¥í•˜ê¸° ìœ„í•œ Dto í´ë˜ìŠ¤

          return new DefaultOAuth2User(
                  Collections.singleton(new SimpleGrantedAuthority(user.getRoleKey())),
                  attributes.getAttributes(),
                  attributes.getNameAttributeKey());
      }

      private User saveOrUpdate(OAuthAttributes attributes)
      {
          User user = userRepository.findByEmail(attributes.getEmail())
                  .map(entity -> entity.update(attributes.getName(), attributes.getPicture()))
                  .orElse(attributes.toEntity());

          return userRepository.save(user);
      }
  }
