          };

          var id = $('#id').val();

          $.ajax({
              type: 'PUT',
              url: '/api/v1/posts/'+id,
              dataType: 'json',
              contentType:'application/json; charset=utf-8',
              data: JSON.stringify(data)
          }).done(function() {
